import BaseMenuItem from './BaseMenuItem';

class BackItem extends BaseMenuItem {
  /**
	 *
	 * @param {Player} player - vjs player
	 * @param {Object} options - options object
	 * @param {SubtitleSettingMenu} subtitleSettingMenu
	 */
  constructor(player, options, subtitleSettingMenu) {
    super(player, options);
    this.menu_ = subtitleSettingMenu;
    this.addClass('vjs-menu-item-back');
  }

  /**
		* Any click on a `BackItem` puts it into the selected state.
		* See {@link BaseMenuItem#handleClick} for instances where this is called.
		*
		* @param {EventTarget~Event} event
		*        The `keydown`, `tap`, or `click` event that caused this function to be
		*        called.
		*
		* @listens tap
		* @listens click
		*/
  handleClick(event) {
    if (this.menu_) {
      this.menu_.hide();
    }
  }
}

export default BackItem;
